import React, { useEffect, useState, useContext } from "react";
import { Link } from "react-router-dom";
import Header from "./Header"; 
import { FavoritesContext } from "../contexts/FavoritesContext";
import { toast, ToastContainer } from 'react-toastify'; 
import 'react-toastify/dist/ReactToastify.css';
import Loading from "./loading";
import ReactPaginate from 'react-paginate';
import { Modal, Button } from 'react-bootstrap';

const API_URL = "https://www.omdbapi.com/?apikey=66f12840";

const Dashboard = () => {
  const [movies, setMovies] = useState([]); 
  const [loading, setLoading] = useState(false); 
  const [error, setError] = useState(null); 
  const [currentPage, setCurrentPage] = useState(1);
  const [totalResults, setTotalResults] = useState(0);
  const [selectedMovie, setSelectedMovie] = useState(null); // State for the selected movie
  const [showModal, setShowModal] = useState(false); // State for modal visibility

  const { favorites, addToFavorites, removeFromFavorites } = useContext(FavoritesContext); 
  const moviesPerPage = 10; 

  // Function to search movies based on title and type 
  const searchMovies = async (title, type = "movie", page = 1) => {
    setError(null);
    if (title === '') {
      setError("Please Enter the Movie Title");
      setLoading(false);
      return false;
    }
    try {
      const response = await fetch(`${API_URL}&s=${encodeURIComponent(title)}&type=${type}&page=${page}`);
      const data = await response.json();
      
      if (data.Response === "True") {
        setMovies(data.Search);
        setTotalResults(parseInt(data.totalResults)); // total number of movies found
      } else {
        setMovies([]);
        setError(data.Error || "No results found.");
      }
    } catch (err) {
      console.error("Error fetching movies:", err);
      setError("An error occurred while fetching data.");
      setMovies([]);
    } finally {
      setLoading(false);
    }
  };

  // Fetch default movies 
  useEffect(() => {
    searchMovies("all", "movie", currentPage); 
  }, [currentPage]);

  // Handle page click from react-paginate
  const handlePageClick = (event) => {
    const selectedPage = event.selected + 1; // React Paginate uses zero-based index
    setCurrentPage(selectedPage);
  };

  // Check if a movie is already in favorites
  const isFavorite = (movieId) => {
    return favorites.some((movie) => movie.imdbID === movieId);
  }

  const handleFavoriteClick = (movie) => {
    if (isFavorite(movie.imdbID)) {
      removeFromFavorites(movie.imdbID);
      toast.error("Removed from Favorites!");
    } else {
      addToFavorites(movie);
      toast.success("Added to Favorites!");
    }
  };

  const openModal = async (movieId) => {
    setLoading(true);
    try {
      const response = await fetch(`${API_URL}&i=${movieId}`);
      const data = await response.json();
      if (data.Response === "True") {
        setSelectedMovie(data);
        setShowModal(true);
      }
    } catch (err) {
      console.error("Error fetching movie details:", err);
      setError("An error occurred while fetching movie details.");
    } finally {
      setLoading(false);
    }
  };

  const closeModal = () => {
    setShowModal(false);
    setSelectedMovie(null);
  };

  if (loading) {
    return <Loading />; 
  }

  return (
    <>
      <Header onSearch={searchMovies} />
      <ToastContainer position="top-right" autoClose={3000} />

      <nav id="navigation">
        <div className="container">
          <div id="responsive-nav">
            <ul className="main-nav nav navbar-nav">
              <li className="active"><Link to="#">Home</Link></li>
              <li><Link to="/favourite">Favourite</Link></li>
              <li><Link to="#">Help</Link></li>
            </ul>
          </div>
        </div>
      </nav>
       
      <div className="section">
        <div className="container">
          <div className="row">
            {error && !loading && <div className="col-md-12"><h2>{error}</h2></div>}

            {!loading && !error && movies.length > 0 && (
              movies.map((movie) => ( 
                <div className="col-md-4 col-xs-6" key={movie.imdbID}>
                  <div className="shop" onClick={() => openModal(movie.imdbID)}>
                    <div className="shop-img">
                      <img
                        src={movie.Poster !== "N/A" ? movie.Poster : "./assets/img/badnews.jpg"}
                        alt={movie.Title}
                        style={{ width: "355px", height: "400px", objectFit: "cover" }}
                      />
                    </div>
                    <br />
                    <span className="card__category">
                      <button
                        type="button"
                        className={`btn btn-sm ${isFavorite(movie.imdbID) ? 'btn-danger' : 'btn-success'}`}
                        style={{
                          padding: "0px 10px",
                          color: "#fff",
                          borderRadius: "3px",
                          height: "30px",
                        }}
                        onClick={(e) => {
                          e.stopPropagation(); // Prevent modal open on button click
                          handleFavoriteClick(movie);
                        }}
                      >
                        {isFavorite(movie.imdbID) ? 'Remove from Favourite' : 'Add to Favourite'}
                      </button>
                    </span>
                  </div>
                </div>
              ))
            )}
          </div>
        </div>
      </div>

      {/* Pagination Component */}
      <ReactPaginate
        previousLabel={'Previous'}
        nextLabel={'Next'}
        breakLabel={'...'}
        pageCount={Math.ceil(totalResults / moviesPerPage)}
        pageRangeDisplayed={3}
        onPageChange={handlePageClick}
        renderOnZeroPageCount={null} 
        containerClassName={'pagination'}
        activeClassName={'active'}
        marginPagesDisplayed={1}
      />

      {/* Movie Details Modal */}
      <Modal show={showModal} onHide={closeModal}>
        <Modal.Header closeButton>
          <Modal.Title>{selectedMovie?.Title}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {selectedMovie && (
            <>
              <img
                src={selectedMovie.Poster !== "N/A" ? selectedMovie.Poster : "./assets/img/badnews.jpg"}
                alt={selectedMovie.Title}
                style={{ width: "100%", marginBottom: "15px" }}
              />
              <p><strong>Year:</strong> {selectedMovie.Year}</p>
              <p><strong>Plot:</strong> {selectedMovie.Plot}</p>
              <p><strong>Director:</strong> {selectedMovie.Director}</p>
            </>
          )}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={closeModal}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>

      {/* Footer */}
      <footer id="footer">
        {/* Footer content here */}
      </footer>
    </>
  );
};

export default Dashboard;

      
