import React, { useState } from "react";
import { Link } from "react-router-dom";
import MovieModal from "./MovieModal";

const Header = ({ onSearch }) => {
  const [searchTerm, setSearchTerm] = useState("");
  const [showModal, setShowModal] = useState(false);
  const [movieData, setMovieData] = useState(null); // Set initial state to null

  const openModal = () => setShowModal(true);
  const closeModal = () => setShowModal(false);

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    const trimmedTerm = searchTerm.trim();
    if (trimmedTerm) {
      try {
        // Call the onSearch prop with the search term (optional)
        onSearch(trimmedTerm);
        
        // Fetch movie details using the API
        const response = await fetch(`https://www.omdbapi.com/?apikey=YOUR_API_KEY&t=${trimmedTerm}`);
        const data = await response.json();
        
        if (data.Response === "True") {
          // Update the state with the fetched movie details
          setMovieData({
            title: data.Title,
            description: data.Plot,
            posterUrl: data.Poster,
          });
          openModal(); // Open the modal after setting movieData
        } else {
          // Handle case where no movie details are found
          alert("Movie not found. Please try another search term.");
        }
      } catch (error) {
        console.error("Error fetching movie data:", error);
      }
    }
  };
   
  const handleInputChange = (event) => {
    const value = event.target.value;
    setSearchTerm(value);
    // Optional: Call the onSearch prop with the current input value
    // onSearch(value);
  };

  return (
    <header>
      {/* Top Header */}
      <div id="top-header">
        <div className="container">
          <ul className="header-links pull-left">
            <li>
              <Link to="#">
                <i className="fa fa-phone"></i> +021-95-51-84
              </Link>
            </li>
            <li>
              <Link to="#">
                <i className="fa fa-envelope-o"></i> email@email.com
              </Link>
            </li>
            <li>
              <Link to="#">
                <i className="fa fa-map-marker"></i> 1734 Stonecoal Road
              </Link>
            </li>
          </ul>
        </div>
      </div>

      {/* Main Header */}
      <div id="header">
        <div className="container">
          <div className="row">
            <div className="col-md-3">
              <div className="header-logo">
                <Link to="#" className="logo">
                  <img src="/assests/img/logo.png" alt="Logo" />
                </Link>
              </div>
            </div>
            {/* Search Bar */}
            <div className="col-md-6">
              <div className="header-search">
                <form onSubmit={handleSubmit}>
                  <select className="search-type-dropdown">
                    <option value="All">All</option>
                    <option value="movie">Movie</option>
                    <option value="series">Series</option>
                    <option value="episode">Episode</option>
                  </select>
                  <input
                    className="input"
                    type="text"
                    placeholder="Search for a movie, TV Series"
                    value={searchTerm}
                    onChange={handleInputChange}
                  />
                  <i className="fa-solid fa-magnifying-glass-location custom-icon icon-spacing"></i>
                </form>
              </div>
            </div>
          </div>
        </div>
      </div>
      <MovieModal show={showModal} handleClose={closeModal} movieData={movieData} />
    </header>
  );
};

export default Header;
