import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import Header from "./Header"; 


// ---- OMDb API URL -----
const API_URL = "https://www.omdbapi.com/?apikey=66f12840";

const Dashboard = () => {
  const [movies, setMovies] = useState([]); 
  const [loading, setLoading] = useState(false); 
  const [error, setError] = useState(null); 

  // Function to search movies based on title
  const searchMovies = async (title) => {
    setLoading(true); 
    setError(null); 
    try {
      const response = await fetch(`${API_URL}&s=${encodeURIComponent(title)}&y=2024`);
      const data = await response.json();

      if (data.Response === "True") {
        setMovies(data.Search); // Update movies state with fetched data
        console.log(data.Search, "Movies fetched!");
      } else {
        setMovies([]); 
        setError(data.Error || "No movies found.");
        console.log("No movies found.");
      }
    } catch (err) {
      console.error("Error fetching movies:", err);
      setError("An error occurred while fetching data.");
      setMovies([]);
    } finally {
      setLoading(false); // End loading
    }
  };

  // Fetch default movies when component mounts
  useEffect(() => {
    searchMovies("Batman"); // Default search term
  }, []);

  return (
    <>
      {/* Header Component with onSearch callback */}
      <Header onSearch={searchMovies} />

      {/* Movies Section */}
      <div className="section">
        <div className="container">
          <div className="row">
            {/* Display loading indicator */}
            {loading && (
              <div className="col-md-12">
                <h2>Loading...</h2>
              </div>
            )}

            {/* Display error message */}
            {error && !loading && (
              <div className="col-md-12">
                <h2>{error}</h2>
              </div>
            )}

            {/* Display movies */}
            {!loading && !error && movies.length > 0 && (
              movies.map((movie) => (
                <div className="col-md-4 col-xs-6" key={movie.imdbID}>
                  <div className="shop">
                    <div className="shop-img">
                      <img
                        src={movie.Poster !== "N/A" ? movie.Poster : "./assests/img/default.jpg"}
                        alt={movie.Title}
                      />
                    </div>
                    <br />
                    <h4 className="card__title">
                      <Link to={`/movie/${movie.imdbID}`}>{movie.Title} ({movie.Year})</Link>
                    </h4>
                    <span className="card__category">
                      <span style={{ width: "40%" }}>
                        <Link to={`/movie/${movie.imdbID}`}>movie</Link>
                      </span>
                      <span style={{ width: "60%", color: "white", textAlign: "right" }}>
                        <button
                          type="button"
                          className="btn btn-success btn-sm"
                          style={{
                            background: "#1f7000",
                            padding: "0px 10px",
                            color: "#fff",
                            borderRadius: "3px",
                            height: "30px",
                          }}
                          onClick={() => {
                            // Add to Favourite functionality (to be implemented)
                          }}
                        >
                          Add to Favourite
                        </button>
                      </span>
                    </span>
                    <span className="card__rate">
                      <i className="fa-solid fa-star"></i> {movie.Year}
                    </span>
                  </div>
                </div>
              ))
            )}
          </div>
        </div>
      </div>

      {/* Footer */}
      <footer id="footer">
        {/* Your existing footer code */}
        <div className="section">
          <div className="container">
            {/* ... */}
          </div>
        </div>

        <div id="bottom-footer" className="section">
          <div className="container">
            <div className="row">
              <div className="col-md-12 text-center">
                <ul className="footer-payments">
                  <li><Link to="#"><i className="fa fa-cc-visa"></i></Link></li>
                  <li><Link to="#"><i className="fa fa-credit-card"></i></Link></li>
                  <li><Link to="#"><i className="fa fa-cc-paypal"></i></Link></li>
                  <li><Link to="#"><i className="fa fa-cc-mastercard"></i></Link></li>
                  <li><Link to="#"><i className="fa fa-cc-discover"></i></Link></li>
                  <li><Link to="#"><i className="fa fa-cc-amex"></i></Link></li>
                </ul>
                <span className="copyright">
                  &copy; {new Date().getFullYear()} All rights reserved | This template is made with
                  <i className="fa fa-heart-o" aria-hidden="true"></i> by{" "}
                  <Link to="https://colorlib.com" target="_blank" rel="noopener noreferrer">
                    Colorlib
                  </Link>
                </span>
              </div>
            </div>
          </div>
        </div>
      </footer>
    </>
  );
};

export default Dashboard;
