import React, { createContext, useState, useEffect } from 'react';

// context create 
export const FavoritesContext = createContext();

// Create a Provider Component
export const FavoritesProvider = ({ children }) => {
  const [favorites, setFavorites] = useState([]);
  
  // get favorites from localStorage 
      useEffect(() => {
        const storedFavorites = localStorage.getItem('react-movie-app-favorites');
        console.log("Loaded favorites:", storedFavorites);
        if (storedFavorites) {
            setFavorites(JSON.parse(storedFavorites));
        }
      }, []);
     console.log("Current favvvvv:", favorites);


  // Save favorites to localStorage 
      useEffect(() => {
        localStorage.setItem('react-movie-app-favorites', JSON.stringify(favorites));
      }, [favorites]);
    

  // Function to add a movie to favorites
  const addToFavorites = (movie) => {
    // Prevent duplicates
    const isAlreadyFavorite = favorites.some((fav) => fav.imdbID === movie.imdbID);
    if (!isAlreadyFavorite) {
      setFavorites([...favorites, movie]);
    }
  };

//  remove a movie from favorites---
  const removeFromFavorites = (movieId) => {
    setFavorites(favorites.filter((movie) => movie.imdbID !== movieId));
  };

  return (
    <FavoritesContext.Provider
     value={{ favorites, addToFavorites, removeFromFavorites }} > {children}
    </FavoritesContext.Provider>
  );
};
