// src/components/Header.js

import React, { useState } from "react";
import { Link } from "react-router-dom";

const Header = ({ onSearch }) => {
  const [searchTerm, setSearchTerm] = useState("");

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault(); // Prevent page reload
    const trimmedTerm = searchTerm.trim();
    if (trimmedTerm) {
      onSearch(trimmedTerm); // Trigger search in parent component
    }
  };

  return (
    <header>
      {/* Top Header */}
      <div id="top-header">
        <div className="container">
          <ul className="header-links pull-left">
            <li>
              <Link to="#">
                <i className="fa fa-phone"></i> +021-95-51-84
              </Link>
            </li>
            <li>
              <Link to="#">
                <i className="fa fa-envelope-o"></i> email@email.com
              </Link>
            </li>
            <li>
              <Link to="#">
                <i className="fa fa-map-marker"></i> 1734 Stonecoal Road
              </Link>
            </li>
          </ul>
        </div>
      </div>

      {/* Main Header */}
      <div id="header">
        <div className="container">
          <div className="row">
            {/* Logo */}
            <div className="col-md-3">
              <div className="header-logo">
                <Link to="#" className="logo">
                  <img src="./assests/img/logo.png" alt="Logo" />
                </Link>
              </div>
            </div>

            {/* Search Bar */}
            <div className="col-md-6">
              <div className="header-search">
                <form onSubmit={handleSubmit}>
                  <input
                    className="input"
                    type="text"
                    placeholder="Search for a movie, TV Series"
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                  />
                  <button className="search-btn" type="submit">
                    Search
                  </button>
                </form>
              </div>
            </div>

            {/* Additional Header Content (e.g., Cart, Account) */}
            <div className="col-md-3 clearfix">
              {/* You can add more elements here if needed */}
            </div>
          </div>
        </div>
      </div>
    </header>
  );
};

export default Header;
