import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom'; 

const API_URL = "https://www.omdbapi.com/";
const API_KEY = "66f12840";

const MovieDetails = () => {
  const { id } = useParams(); 
  const [movie, setMovie] = useState(null); 
  

  // Fetch movie details 
  useEffect(() => {
    const fetchMovieDetails = async () => {
      try {
        const response = await fetch(`${API_URL}?apikey=${API_KEY}&i=${id}&plot=full`);
        const data = await response.json();

        if (data.Response === "True") {
          setMovie(data);
        } else {
          console.error("Movie not found");
        }
      } catch (error) {
        console.error("Error fetching movie details:", error);
      } 
    };
    fetchMovieDetails(); 
  }, [id]);

  if (!movie) {
    return <div>No movie details available.</div>; 
  }

   // Function to render ratings based on content type
  //  const renderRatings = (movie) => {
  //   if (!movie.Ratings || movie.Ratings.length === 0) {
  //     return <p>N/A</p>;
  //   }
  //   return (
  //     <ul>
  //       {movie.Ratings.map((rating, index) => (
  //         <li key={index}><strong>{rating.Source}:</strong> {rating.Value}</li>
  //       ))}
  //     </ul>
  //   );
  // };

  return (
    <div>
      {/* Header Section */}
      <header>
        <div id="top-header">
          <div className="container">
            <ul className="header-links pull-left">
              <li><Link to="#"><i className="fa fa-phone"></i> +021-95-51-84</Link></li>
              <li><Link to="#"><i className="fa fa-envelope-o"></i> email@email.com</Link></li>
              <li><Link to="#"><i className="fa fa-map-marker"></i> 1734 Stonecoal Road</Link></li>
            </ul>
          </div>
        </div>
      
        {/* Main Header */}
        <div id="header">
          <div className="container">
            <div className="row">
              <div className="col-md-3">
                <div className="header-logo">
                  <Link to="/" className="logo">
                    <img src="/assests/img/logo.png" alt="Logo" />
                  </Link>
                </div>
              </div>
            </div>
          </div>
        </div>
      </header>

