import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom'; 

const API_URL = "https://www.omdbapi.com/";
const API_KEY = "66f12840";

const MovieDetails = () => {
  const { id } = useParams(); 
  const [movie, setMovie] = useState(null); 
  

  // Fetch movie details 
  useEffect(() => {
    const fetchMovieDetails = async () => {
      try {
        const response = await fetch(`${API_URL}?apikey=${API_KEY}&i=${id}&plot=full`);
        const data = await response.json();

        if (data.Response === "True") {
          setMovie(data);
        } else {
          console.error("Movie not found");
        }
      } catch (error) {
        console.error("Error fetching movie details:", error);
      } 
    };
    fetchMovieDetails(); 
  }, [id]);

  if (!movie) {
    return <div>No movie details available.</div>; 
  }

   // Function to render ratings based on content type
  //  const renderRatings = (movie) => {
  //   if (!movie.Ratings || movie.Ratings.length === 0) {
  //     return <p>N/A</p>;
  //   }
  //   return (
  //     <ul>
  //       {movie.Ratings.map((rating, index) => (
  //         <li key={index}><strong>{rating.Source}:</strong> {rating.Value}</li>
  //       ))}
  //     </ul>
  //   );
  // };

  return (
    <div>
      {/* Header Section */}
      <header>
        <div id="top-header">
          <div className="container">
            <ul className="header-links pull-left">
              <li><Link to="#"><i className="fa fa-phone"></i> +021-95-51-84</Link></li>
              <li><Link to="#"><i className="fa fa-envelope-o"></i> email@email.com</Link></li>
              <li><Link to="#"><i className="fa fa-map-marker"></i> 1734 Stonecoal Road</Link></li>
            </ul>
          </div>
        </div>
      
        {/* Main Header */}
        <div id="header">
          <div className="container">
            <div className="row">
              <div className="col-md-3">
                <div className="header-logo">
                  <Link to="/" className="logo">
                    <img src="/assests/img/logo.png" alt="Logo" />
                  </Link>
                </div>
              </div>
            </div>
          </div>
        </div>
      </header>

      {/* Movie Details Section */} 
      <div style={{
                    display: 'flex', 
                    justifyContent: 'space-between',
                    padding: '30px' 
                    }}>
        <div className='fade-in' style={{ marginRight: '60px' }}>
          <img 
            src={movie.Poster && movie.Poster !== "N/A" ? movie.Poster : "https://via.placeholder.com/300x450"} 
            alt={`${movie.Title} poster`} 
            // for the poster
            style={{ width: '350px', height: '450px', borderRadius: '20px',}} 
          />
        </div>
        
        <div  className =" fade-in" style={{ flex: 1 }}> {/* This div will take the remaining space */}
          <h1>{movie.Title || "Title not available"} ({movie.Year || "Year not available"})</h1>
          <p><strong>Director:</strong> {movie.Director || "N/A"}</p>
          <p><strong>Plot:</strong> {movie.Plot || "N/A"}</p>
          <p><strong>Genre:</strong>{movie.Genre || "N/A"}</p>
          <p><strong>Rating:</strong> {movie.imdbRating || "N/A"}</p>
          <div class="sc-gmPhUn">
            <span aria-label="IMDb Rating 8.8" 
            class="ipc-rating-star ipc-rating-star--baseAlt ipc-rating-star--imdb sc-iHbSHJ GjGoZ titleaction--imdbstar">
              <svg width="24" 
              height="24" xmlns="http://www.w3.org/2000/svg" 
              class="ipc-icon ipc-icon--star-inline" viewBox="0 0 24 24" fill="currentColor"
              role="presentation"><path d="M12 20.1l5.82 3.682c1.066.675 2.37-.322 2.09-1.584l-1.543-6.926 5.146-4.667c.94-.85.435-2.465-.799-2.567l-6.773-.602L13.29.89a1.38 1.38 0 0 0-2.581 0l-2.65 6.53-6.774.602C.052 8.126-.453 9.74.486 10.59l5.147 4.666-1.542 6.926c-.28 1.262 1.023 2.26 2.09 1.585L12 20.099z">
            </path>
            </svg>
            <span class="ipc-rating-star--rating">8.8/10</span>
            </span>
            {/* for the rating button  */}
            <span class="sc-hRJfrW hmfHfm">
            </span><button aria-label="Rate Fight Club" class="ipc-rate-button sc-klVQfs dAyVwh titleaction--userstar ipc-rate-button--unrated ipc-rate-button--baseAlt"
             data-testid="rate-button">
              <span class="ipc-rating-star ipc-rating-star--baseAlt ipc-rating-star--rate"><svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" class="ipc-icon ipc-icon--star-border-inline" viewBox="0 0 24 24" fill="currentColor" role="presentation">
              <path d="M22.724 8.217l-6.786-.587-2.65-6.22c-.477-1.133-2.103-1.133-2.58 0l-2.65 6.234-6.772.573c-1.234.098-1.739 1.636-.8 2.446l5.146 4.446-1.542 6.598c-.28 1.202 1.023 2.153 2.09 1.51l5.818-3.495 5.819 3.509c1.065.643 2.37-.308 2.089-1.51l-1.542-6.612 5.145-4.446c.94-.81.45-2.348-.785-2.446zm-10.726 8.89l-5.272 3.174 1.402-5.983-4.655-4.026 6.141-.531 2.384-5.634 2.398 5.648 6.14.531-4.654 4.026 1.402 5.983-5.286-3.187z"></path></svg><span class="ipc-rating-star--rate">Rate</span></span></button>
            </div>
        </div>
        
      </div>

      {/* Footer Section */}
      <footer id="footer">
        <div className="section">
          <div className="container">
            <div className="row">
              {/* About Us */}
              <div className="col-md-3 col-xs-6">
                <div className="footer">
                  <h3 className="footer-title">About Us</h3>
                  <ul className="footer-links">
                    <li><Link to="#"><i className="fa fa-map-marker"></i>1734 Stonecoal Road </Link></li>
                    <li><Link to="#"><i className="fa fa-phone"></i>+021-95-51-84</Link></li>
                    <li><Link to="#"><i className="fa fa-envelope-o"></i>email@email.com</Link></li>
                  </ul>
                </div>
              </div>

              {/* Categories */}
              <div className="col-md-3 col-xs-6">
                <div className="footer">
                  <h3 className="footer-title">Categories</h3>
                  <ul className="footer-links">
                    <li><Link to="#">Bollywood movie</Link></li>
                    <li><Link to="#">Hollywood movies</Link></li>
                  </ul>
                </div>
              </div>
              <div className="clearfix visible-xs"></div>

              {/* Legal */}
              <div className="col-md-3 col-xs-6">
                <div className="footer">
                  <h3 className="footer-title">Legal</h3>
                  <ul className="footer-links">
                    <li><a href="#">Terms of Use</a></li>
                    <li><a href="#">Privacy Policy</a></li>
                    <li><a href="#">Security</a></li>
                  </ul>
                </div>
              </div>

              {/* Download Our App */}
              <div className="col-md-3 col-xs-6">
                <div className="footer">
                  <h3 className="footer-title">Download Our App</h3>
                  <ul className="footer__app">
                  <li><Link to="#"><img src="/assests/img/store.svg" alt="store"/></Link></li> <br></br>
                  <li><Link to ="#"><img src="/assests/img/google-play-badge.png" alt="" className="custom-logo"/></Link></li>
                  </ul>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Bottom Footer */}
        <div id="bottom-footer" className="section">
          <div className="container">
            <div className="row">
              <div className="col-md-12 text-center">
                <ul className="footer-payments">
                  <li><Link to="#"><i className="fa fa-cc-visa"></i></Link></li>
                  <li><Link to="#"><i className="fa fa-credit-card"></i></Link></li>
                  <li><Link to="#"><i className="fa fa-cc-paypal"></i></Link></li>
                  <li><Link to="#"><i className="fa fa-cc-mastercard"></i></Link></li>
                  <li><Link to="#"><i className="fa fa-cc-discover"></i></Link></li>
                  <li><Link to="#"><i className="fa fa-cc-amex"></i></Link></li>
                </ul>
                <span className="copyright">
                  &copy; {new Date().getFullYear()} All rights reserved | This template is made with 
                  <i className="fa fa-heart-o" aria-hidden="true"></i>
                  by <Link to="https://colorlib.com" target="_blank" rel="noopener noreferrer">Colorlib</Link>
                </span>
              </div>
            </div>
          </div>
        </div>
      </footer>
    </div> 
  );
};

export default MovieDetails;
