import React, { useEffect, useState, useContext } from "react";
import { Link } from "react-router-dom";
import Header from "./Header"; 
import { FavoritesContext } from "../contexts/FavoritesContext";
import { toast, ToastContainer } from 'react-toastify'; 
import 'react-toastify/dist/ReactToastify.css';
import Loading from "./loading";


const API_URL = "https://www.omdbapi.com/?apikey=66f12840";

const Dashboard = () => {
  const [movies, setMovies] = useState([]); 
  const [loading, setLoading] = useState(false); 
  const [error, setError] = useState(null); 
  const [currentPage, setCurrentPage] = useState(1);
  const [totalResults, setTotalResults] = useState(0);

  const { favorites, addToFavorites, removeFromFavorites } = useContext(FavoritesContext); 
  
  const searchMovies = async (title, type = "movie", page = 1) => {
    setLoading(true); 
    setError(null); 
    if(title === ''){
      setError("Please Enter the Movie Title");
      setLoading(false); 
      return false;
    }
    try {
      const response = await fetch(`${API_URL}&s=${encodeURIComponent(title)}&type=${type}&y=2024&page=${page}`);
      const data = await response.json();

      if (data.Response === "True") {
        setMovies(data.Search); 
        setTotalResults(parseInt(data.totalResults));
      } else {
        setMovies([]); 
        setError(data.Error || "No results found.");
      }
    } catch (err) {
      setError("An error occurred while fetching data.");
      setMovies([]);
    } finally {
      setLoading(false); 
    }
  };

  useEffect(() => {
    searchMovies("all", "movie", currentPage); 
  }, [currentPage]);

  const isFavorite = (movieId) => {
    return favorites.some((movie) => movie.imdbID === movieId);
  };

  const handleFavoriteClick = (movie) => {
    if (isFavorite(movie.imdbID)) {
      removeFromFavorites(movie.imdbID);
      toast.error("Removed from Favorites!");
    } else {
      addToFavorites(movie);
      toast.success("Added to Favorites!");
    }
  };

  const handleNextPage = () => {
    if (currentPage < Math.ceil(totalResults / 10)) {
      setCurrentPage((prevPage) => prevPage + 1);
    }
  };

  const handlePrevPage = () => {
    if (currentPage > 1) {
      setCurrentPage((prevPage) => prevPage - 1);
    }
  };

  if (loading) {
    return <Loading />;
  }

  return (
    <>
      <Header onSearch={searchMovies} />
      <ToastContainer position="top-right" autoClose={3000} />
      <nav id="navigation">
        {/* Navigation code */}
      </nav>
      <div className="section">
        <div className="container">
          <div className="row">
            {loading && (
              <div className="col-md-12">
                <img src={"../assests/img/load.gif"} alt="Loading..." style={{ width: "50px", height: "50px" }} />
              </div> 
            )}
            {error && !loading && <div className="col-md-12"><h2>{error}</h2></div>}
            {!loading && !error && movies.length > 0 && (
              movies.map((movie) => ( 
                <div className="col-md-4 col-xs-6" key={movie.imdbID}>
                  <div className="shop">
                    {/* Movie rendering code */}
                  </div>
                </div>
              ))
            )}
          </div>
          <div className="pagination">
            <button onClick={handlePrevPage} disabled={currentPage === 1}>Previous</button>
            <span>Page {currentPage}</span>
            <button onClick={handleNextPage} disabled={currentPage >= Math.ceil(totalResults / 10)}>Next</button>
          </div>
        </div>
      </div>
      {/* Footer */}
    </>
  );
};

export default Dashboard;
